define("widget/user-multiselect-commons",["aui","jquery","exports"],function(B,C,A){A.avatarSize=stash.widget.avatarSizeInPx({size:"xsmall"});A.containerCssClass="users-autocomplete";A.dropdownCssClass="users-dropdown";A.fetchLimit=10;A.minimumInputLength=1;A.quietMillis=300;A.filterSearchTerm=function(D){return C.trim(D).replace(/^@/,"")};A.formatInputTooShort=function(){return B.escapeHtml(stash_i18n("stash.web.user-multiselect.help","Start typing to search for a user"))};A.formatUserSelection=function(D){return stash.widget.avatarWithName({size:"xsmall",person:D})};A.formatGroupSelection=function(D){return stash.widget.groupAvatarWithName({size:"xsmall",name:D.name})};A.formatUserResult=function(D){return stash.widget.avatarWithNameAndEmail({size:"small",person:D})};A.formatGroupResult=function(D){return stash.widget.groupAvatarWithName({size:"small",name:D.name})};A.showNoResultsIfAllDisabled=function(E){var D=C(".select2-drop-active > .select2-results");if(!D.length){return }if(D.children(".select2-result-selectable").length===0&&D.children(".select2-disabled").length){D.append('<li class="select2-no-results">'+E()+"</li>")}}});